import { useMutation } from '@apollo/client'
import React from 'react'
import { PUBLISH_USER } from '../../../../../graphql-client/Mutation'
import { useState } from 'react'
import {openUploadWidget} from '../../../../../cloudinaryService/CloudinaryService'

export default function AvatarForm() {
    const [avatarForm, setAvatarForm] = useState({
       avatarForm:'', 
       
       })
       const [settingUser] = useMutation(PUBLISH_USER,{
         variables:{
           
           avatar: avatarForm.avatarForm,
           
           
         }
       })
       const beginUpload = tag => {
         const uploadOptions = {
           cloudName: "nhk598",
           tags: [tag],
           uploadPreset: "tailen"
         };
       
         openUploadWidget(uploadOptions, (error, photos) => {
           if (!error) {
             // console.log(photos.info.thumbnail_url);
             if(photos.event === 'success'){
               setFromSettingUser({...fromSettingUser,avatarForm: photos.info.secure_url});
               // setFormState({...formState,thumbnail: photos.info.thumbnail_url})
               
             }
             
           } else {
             console.log(error);
           }
         })
       }
    return (
        <div className="form-group">
                    <label htmlFor="manageAvatar">Chọn ảnh đại diện</label>
                    <button value={settingUser.avatarForm} onClick={() => beginUpload()}>chon anh dai dien</button>
                                      
                  </div>
    )
}
