import React from 'react'
import { useQuery } from '@apollo/client';
import { URL_VIDEO_PLAYING } from '../../../../graphql-client/Queries';
import {
    useParams
  } from "react-router-dom";
  
export default function WVideoLeftTop() {
    let {id} = useParams();
    const {loading, error, data}=useQuery(URL_VIDEO_PLAYING,{
    variables: {id},
});
    if(loading) return <p>loading...</p>;
    if (error) return `Error! ${error.message}`;
//   console.log(data.video.url);
const videoJsOptions = { // lookup the options in the docs for more options
    autoplay: true,
    controls: true,
    responsive: true,
    fluid: true,
    sources: [{
      src: '/path/to/video.mp4',
      type: 'video/mp4'
    }]
  }
    return (
        
        <video
        id="my-video"
        class="video-js"
        controls
        preload="auto"
        width="740"
        height="450"
        poster="MY_VIDEO_POSTER.jpg"
        data-setup="{}"
      >
        <source src={data.video.url} type="video/mp4" />
        <source src={data.video.url} type="video/webm" />
        <p class="vjs-no-js">
          To view this video please enable JavaScript, and consider upgrading to a
          web browser that
          <a href="https://videojs.com/html5-video-support/" target="_blank"
            >supports HTML5 video</a
          >
        </p>
      </video>
    
    )
}
