import { useMutation } from '@apollo/client'
import React from 'react'
import { useParams } from 'react-router-dom'
import {NEW_COMMENT} from '../../../../../../../../graphql-client/Mutation'
import { useState } from 'react'
export default function NewComment() {
    const {id} = useParams
    const [formTextComment, setFormTextComment] = useState('')
    const [new_comment] = useMutation(NEW_COMMENT, {
        variables:{videoId: id, text: formTextComment}
    })
    return (
        <div className="d-flex-column newComment">
            <div className="headerNewComment d-flex input-group ">
                <span className="input-group-text" id="basic-addon1">
                <img className="img- video_avatar" alt="" src="./gai.jpg" />
                </span>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        createLink();
                    }}
                    
                    >
                        <input type="text" style={{height: '50px'}} className="form-control" placeholder="Đặt câu hỏi hoăc bình luận của bạn..." aria-label="Username" aria-describedby="basic-addon1" />
                        <div className="d-flex " style={{marginTop: '10px'}}>
                            <button className="btn  me-md-2 ms-auto" type="button">Gửi</button>
                            <button type= "supmid" className="btn " type="button">Hủy</button>
                        </div>
                    </form>
                
            </div>
            <div className="d-flex " style={{marginTop: '10px'}}>
                <button className="btn  me-md-2 ms-auto" type="button">Gửi</button>
                <button className="btn " type="button">Hủy</button>
            </div>
            </div>
    )
}
