import { gql } from '@apollo/client'

const recommendedVideos = gql `
 query recommendedVideos {
     videos {
         title
         id
         thumbnail
         createdAt
         videoLikes{
             id
         }
         view{
             id
         }
         user{
             username
             
         }
     }
 }
`
    // const getNameVideo = gql `
    // query get
    // `

const videoPlaying = gql `
query videoPlaying($id:ID!) {
    video(id:$id){
        id      
        createdAt    
        title
        description
        url
        thumbnail
        document
        userId
        user{
            id
            username
            avatar
        }
        videoLikes{
            id
        }
        view{
            id
        }
        comment{
            id
            text
        }
        
    }
}
`
export { recommendedVideos, videoPlaying }
