import React,{ useState } from 'react'
import VideoCard from './videoCard/VideoCard'
import { useQuery } from '@apollo/client'
import {recommendedVideos} from '../../../graphql-client/Queries'


export default function Home_right() {
    const {loading, error, data} = useQuery(recommendedVideos);
    if (loading) return <p>loading...</p>;
    if (error) return `Error! ${error.message}`;
    const [countViews, setCountViews]= useState(0);
    
    return (
        
            <div className="col-xl-10 col-mb-10 col-sm-10">
                <div className="recommendedVideos">
                    <h2 style={{marginTop: '50px'}}>Recommended</h2>
                    <div className="recommendedVideos_videos">
                        <div className="container-fluid">
                            <div className="row">
                               {data.videos.map((video)=>{
                                //    const a = data.video.videoLikes.length
                               
                                setCountViews=(data.video.videoLikes.length);
                                   return (
                                        <VideoCard
                                         key={video.id}
                                          title ={video.title}
                                          username={video.user.username}
                                          date={video.createdAt}
                                        //   views={CountViews}
                                            like={countViews}
                                          >
                                           
                                        </VideoCard>
                                   )
                               })}
                               {/* <VideoCard></VideoCard> */}
                                
                                
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        
    )
}
