import React from 'react'
import { Route, Switch} from "react-router-dom";
import Home from '../component/home/Home';
import WatchingVideo from '../component/watching/WatchingVideo'
import NewsFeed from '../component/newsFeed/NewsFeed'
import UserSub from '../component/userSub/UserSub'
import VideoFollow from '../component/videoFollow/VideoFollow';
import User from '../component/user/User';
export default function DieuHuongUrl() {
    const routes = [
        {
          path: "/",
          exact: true,
          main: () => <Home/>
        },
        {
          path: "/watching/:id",
          main: () => <WatchingVideo/>
        },
        {
          path: "/news",
          main: () => <NewsFeed/>
        },
        {
            path: "/userFollow",
            main: () => <UserSub/>
          },
          {
            path: "/videoFollow",
            main: () => <VideoFollow/>
          },
          {
            path: "/user/:idUser",
            main: () => <User/>
          }
      ];
      function RouteWithSubRoutes(route) {
        return (
          <Route
            path={route.path}
            render={props => (
              // pass the sub-routes down to keep nesting
              <route.component {...props} routes={route.routes} />
            )}
          />
        );
      }
        return (
            // <div>  
                <Switch>
                    {routes.map((route, index) => (
                            <RouteWithSubRoutes key={i} {...route} />
                    ))}
                </Switch>
            // </div>
           
        )
    }
