import React from 'react'
import {useState} from 'react'
import { CREATE_VIDEO } from '../../../../../graphql-client/Mutation';
import { useMutation } from '@apollo/client';
export default function FormNewVideo() {
    const [formState, setFormState] = useState({
        description: '',
        url: '', 
        title:'',
        document:'',
        thumbnail:''
      });
      const [createVideo] = useMutation(CREATE_VIDEO, {
        variables: {
          description: formState.description,
          url: formState.url,
          title:formState.title,
          document:formState.document,
          thumbnail:formState.thumbnail
        }
      });
    return (
        <div className="modal fade" id="newVideoModal" tabIndex={-1} aria-labelledby="newVideoModalModalLabel" aria-hidden="true">
                            <div className="modal-dialog">
                            <div className="modal-content">
                                <div className="modal-header">
                                <h5 className="modal-title" id="newVideoModalModalLabel">Tải lên video mới của bạn</h5>
                                </div>
                                <div className="modal-body">
                                <form onSubmit={(e) => {
                                    e.preventDefault();
                                    createVideo
                                    }}>
                                    <div className="form-group">
                                    <label htmlFor="new-Video">Chọn Video của bạn</label>
                                    <input type="file" className="form-control-file" id="new-Video" value={formState.url}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            description: e.target.value
                                        })
                                        }/>
                                    </div>
                                    <div className="mb-3">
                                    <label htmlFor="titleNewVideo-from" className="col-form-label">Tiêu đề vủa Video</label>
                                    <textarea className="form-control" id="titleNewVideo-from" style={{height: '50px'}} value={formState.title}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            description: e.target.value
                                        })
                                        } />
                                    </div>
                                    <div className="mb-3">
                                    <label htmlFor="descriptionNewVideo-from" className="col-form-label">Miêu tả video</label>
                                    <textarea className="form-control" id="descriptionNewVideo-from" style={{height: '100px'}} value={formState.description}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            description: e.target.value
                                        })
                                        } />
                                    </div>
                                    <hr />
                                    <h3>Tài liệu Video</h3>
                                    <div className="mb-3">
                                    <label htmlFor="docsNewVideo-from" className="col-form-label">Thêm tài liệu</label>
                                    <textarea className="form-control" placeholder="Nhập vào tài liệu của bạn" id="docsNewVideo-from" style={{height: '100px'}} 
                                    defaultValue={"Không có tài liệu"}
                                    value={formState.description}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            description: e.target.value
                                        })
                                        } 
                                     />
                                    </div>
                                    <div className="form-group">
                                    <label htmlFor="file-Video">Chọn tài liệu từ máy của bạn</label>
                                    <input type="file" className="form-control-file" id="file-Video" />
                                    </div>
                                </form>
                                </div>
                                <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                <button type="submit" className="btn btn-primary">Gửi</button>
                                </div>
                            </div>
                            </div>
                        </div>
    )
}
