import React from 'react'
import { useMutation, useQuery } from '@apollo/client';
import { URL_VIDEO_PLAYING } from '../../../../graphql-client/Queries';
import {
    useParams
  } from "react-router-dom";
import { CREAT_VIEW } from '../../../../graphql-client/Mutation';
  
export default function WVideoLeftTop() {
    let {id} = useParams();
    const [creat_view] = useMutation(CREAT_VIEW,{
      variables:{
        videoId: id
      }
    })
    const {loading, error, data}=useQuery(URL_VIDEO_PLAYING,{
    variables: {id},
});
    if(loading) return <p>loading...</p>;
    if (error) return `Error! ${error.message}`;
    
  console.log(data.video.url);
    return (
            <video onClick={creat_view} width="100%" height="100%" controls className="videoPlay mx-auto" style={{maxHeight: '480px'}}>
                <source src={data.video.url} type="video/mp4" />
                <source src={data.video.url} type="video/ogg" />
                Trình duyệt của bạn không hỗ trợ thẻ video.
            </video>
    )
}
