import React, { Component } from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
// import Home from './component/home/Home';
// import WhatchingVideo from './component/whatching/WhatchingVideo';
import Header from "./component/nav/Header";
import DieuHuongUrl from "./router/DieuHuongUrl";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider, 
  HttpLink
} from "@apollo/client";
// Tạo Liên kết truy vấn cố định
import { createPersistedQueryLink } from "@apollo/client/link/persisted-queries";
// Hàm băm
import { sha256 } from 'crypto-hash';
import NewsFeed from './component/newsfeed/Newsfeed';

const linkChain = createPersistedQueryLink({ 
  sha256, 
  useGETForHashedQueries: true
}).concat(
  new HttpLink({ uri: "http://localhost:4000/" }));
//  const linkChain = {uri: "http://localhost:4000/"};
const client = new ApolloClient({
  cache: new InMemoryCache(),
  // uri: "http://localhost:4000/" 
  link: linkChain,
});


function App() {
  return (
<ApolloProvider client={client}>
    <Router>

      <div className="container-fluid">
        <Header></Header>
        {/* <div>
          <Link to="/hello"></Link>
        </div> */}
        {/* <Home></Home> */}
        {/* <WhatchingVideo></WhatchingVideo> */}
        <NewsFeed/>
        <DieuHuongUrl></DieuHuongUrl>
      </div>

    </Router>
</ApolloProvider>


  );
}

export default App;
