import { gql } from '@apollo/client'
const SIGNUP = gql `
mutation Signup (
    $email: String!
    $password: String!
    $name: String!
){
    signup(
        email: $email
        password: $password
        username: $name
    ){
        token
    }
}
`;

const LOGIN = gql `
mutation Login(
    $email: String!
    $password: String!
){
    login(email: $email, password: $password){
        token
    }
}
`;

const CREATE_VIDEO = gql `
mutation createVideo(
    $title: String
    $description: String
    $url: String!
    $thumbnail: String!
    $document: String
    

   
){
    createVideo(title:$title, description:$description, url:$url, thumbnail: $thumbnail, document: $document){
        id
        title
        user{
        id
        username
        }
    }
}
`;
export { SIGNUP, LOGIN, CREATE_VIDEO }
