         title
         id
         thumbnail
         createdAt
         videoLikes{
             id
         }
         view{
             id
         }
         user{
             username
             avatar
         }
     }
 }
`

const videoPlaying = gql `
query videoPlaying($id:ID!) {
    video(id:$id){
        id      
        createdAt    
        title
        description
        url
        thumbnail
        document
        userId
        user{
            id
            username
            avatar
        }
        videoLikes{
            id
        }
        view{
            id
        }
        comment{
            id
            text
        }
        
    }
}
`

const getIntroduceVideo = gql `
query getIntroduceVideo($id:ID!){
