            id
            username
            avatar
        }
    }
}
`

const getCommentVideo = gql `
query getCommentVideo($id:ID!){
    video(id:$id){
        comment{
            id
            text
            user{
                id
                username
                avatar
            }
        }
    }
}
`


const getDateIntVideo = gql `
query getDateIntVideo($id:ID!){
    video(id:$id){
        createdAt  
        title
        document
        videoLikes{
            id
        }
        view{
            id
        }
        
    }
}
`
export { videosRecommended, videoPlaying, getDateIntVideo, getCommentVideo, getIntroduceVideo, }
