import React from 'react'
import {useState} from 'react'
// import { CREATE_VIDEO } from '../../../../../graphql-client/Mutation';
// import { useMutation } from '@apollo/client';
import FormTitleVideo from './formTitleVideo/FormTitleVideo';
import { fetchPhotos, openUploadWidget } from "../../../../../cloudinaryService/CloudinaryService";

export default function FormNewVideo() {
    const [formState, setFormState] = useState({
       
        add:{
          url: '',
          thumbnail:''
        }
      });
      // const [createVideo] = useMutation(CREATE_VIDEO, {
      //   variables: {
      //     description: formState.description,
      //     url: formState.add.url,
      //     title:formState.title,
      //     document:formState.document,
      //     thumbnail:formState.add.thumbnail
      //   }
      // });
      const beginUpload = tag => {
        const uploadOptions = {
          cloudName: "nhk598",
          tags: [tag],
          uploadPreset: "tailen"
        };
      
        openUploadWidget(uploadOptions, (error, video) => {
          if (!error) {
            console.log(video.info.thumbnail_url);
            if(video.event === 'success'){
              setFormState({...formState,add: {url:video.info.secure_url, thumbnail:video.info.thumbnail_url}});
              // setFormState({...formState,thumbnail: video.info.thumbnail_url})
              
            }
            
          } else {
            console.log(error);
          }
        })
      }
    return (
        
        <div className="modal fade" id="newVideoModal" tabIndex={-1} aria-labelledby="newVideoModalModalLabel" aria-hidden="true">
                            <div className="modal-dialog">
                            <div className="modal-content">
                                <div className="modal-header">
                                <h5 className="modal-title" id="newVideoModalModalLabel">Tải lên video mới của bạn</h5>
                                </div>
                                <div className="modal-body">
                                {/* <form onSubmit={(e) => {
                                    e.preventDefault();
                                    createVideo();
                                   
                                    }}> */}
                                          
                                    <div className="form-group">
                                 
                                    <label htmlFor="new-Video">Chọn Video của bạn</label>
                                    <button value={formState.add.url} onClick={() => beginUpload()}>Upload Video</button>
                                      
                                   
                                    </div>
                                    <FormTitleVideo urlVideo={formState.add.url} urlThumbnail={formState.add.thumbnail}></FormTitleVideo>
                                    {/* <div className="mb-3">
                                    <label htmlFor="titleNewVideo-from" className="col-form-label">Tiêu đề vủa Video</label>
                                    <input type="text" className="form-control" id="titleNewVideo-from" style={{height: '50px'}} 
                                    value={formState.title}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            title: e.target.value
                                        })
                                        } 
                                        />
                                    </div>
                                    
                                    <div className="mb-3">
                                    <label htmlFor="descriptionNewVideo-from" className="col-form-label">Miêu tả video</label>
                                    <input type="text" className="form-control" id="descriptionNewVideo-from" style={{height: '100px'}} 
                                    value={formState.description}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            description: e.target.value
                                        })
                                        }
                                         />
                                    </div>
                                    <hr />
                                    <h3>Tài liệu Video</h3>
                                    <div className="mb-3">
                                    <label htmlFor="docsNewVideo-from" className="col-form-label">Thêm tài liệu</label>
                                    <textarea type="text" className="form-control" placeholder="Nhập vào tài liệu của bạn" id="docsNewVideo-from" style={{height: '100px'}} 
                                   
                                    value={formState.document}
                                        onChange={(e) =>
                                        setFormState({
                                            ...formState,
                                            document: e.target.value
                                        })
                                        } 
                                     />
                                    </div> */}
                                    
                                    {/* <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                    <button type="submit" className="btn btn-primary" >Gửi</button>
                                    </div> */}
                                {/* </form> */}
                                </div>
                                
                            </div>
                            </div>
                        </div>
    )
}
