import { useMutation } from '@apollo/client'
import React from 'react'
import { PUBLISH_USER } from '../../../../graphql-client/Mutation'
import { useState } from 'react'
import {openUploadWidget} from '../../../../cloudinaryService/CloudinaryService'
export default function Setting() {
 const [fromSettingUser, setFromSettingUser] = useState({
   fromName:'',
   fromAvatar:'', 
   fromCover:'',
   fromAbout:'',
  
  })
  const [settingUser] = useMutation(PUBLISH_USER,{
    variables:{
      username:fromSettingUser.fromName,
      avatar: fromSettingUser.fromAvatar,
      cover: fromSettingUser.fromCover,
      about: fromSettingUser.fromAbout
    }
  })
  const beginUpload = tag => {
    const uploadOptions = {
      cloudName: "nhk598",
      tags: [tag],
      uploadPreset: "tailen"
    };
  
    openUploadWidget(uploadOptions, (error, photos) => {
      if (!error) {
        // console.log(photos.info.thumbnail_url);
        if(photos.event === 'success'){
          setFromSettingUser({...fromSettingUser,avatar: photos.info.secure_url});
          // setFormState({...formState,thumbnail: photos.info.thumbnail_url})
          
        }
        
      } else {
        console.log(error);
      }
    })
  }
    return (
        <div className="tab-pane" id="settings" role="tabpanel" aria-labelledby="settings-tab">
          <div className="container">
            <div className="row ">
              <hr />
              <div className="col-12 formdangky mx-auto">
                <div className="form-group">
                  <form action="submit"
                  onSubmit={(e) => {
                    e.preventDefault();
                    settingUser();
                    
                  }}
                  >
                  <h2>Thông tin cá nhân</h2>
                  
                  <div className="form-group">
                    <label htmlFor="manageAvatar">Chọn ảnh đại diện</label>
                    <button value={settingUser.fromAvatar} onClick={() => beginUpload()}>chon anh dai dien</button>
                                      
                  </div>
                  <hr />
                  <div className="form-group">
                    <label htmlFor="manageCover">Chọn ảnh bìa</label>
                    <button value={settingUser.froma} onClick={() => beginUpload()}>chon anh dai bia</button>
                  </div>
                  <hr />
                  <label htmlFor="inputName">Nhập tên</label>
                  <input type="text" name="inputName" id="inputName" className="form-control" placeholder="tên của bạn là" aria-describedby="helpId" />
                  <div className="form-group">
                    <label htmlFor="inputEmail">Nhập địa  chỉ email</label>
                    <input type="email" className="form-control" name="inputEmail" id="inputEmail" aria-describedby="emailHelpId" placeholder="email@gmail.com" 
                    value={fromSettingUser.fromName}
                    onChange={(e) =>
                      setFromSettingUser({
                        ...fromSettingUser,
                        fromName: e.target.value
                      })
                    }
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="settingIntroduceChannels-text" className="col-form-label">Giới thệu kênh:</label>
                    <textarea className="form-control" id="settingIntroduceChannels-text" style={{height: '100px'}} defaultValue={""} 
                    value={fromSettingUser.fromAbout}
                    onChange={(e) =>
                      setFromSettingUser({
                        ...fromSettingUser,
                        fromAbout: e.target.value
                      })
                    }
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="introduceChannels-from" className="col-form-label">Địa chỉ liên hệ:</label>
                    <textarea className="form-control" id="introduceChannels-from" style={{height: '50px'}} defaultValue={""} />
                  </div>
                  <div className="form-group">
                    <label htmlFor="myDate2">Năm sinh</label>
                    <input type="date" id="myDate2" className="form-control" min="1800-01-01" max="2024-12-30" defaultValue="0000-00-00" />
                  </div>
                  <div className="form-check">
                    <label htmlFor="flexRadioDefault1">chọn giới</label>
                    <br />
                    <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" />
                    <label className="form-check-label" htmlFor="flexRadioDefault1">
                      Nam
                    </label>
                  </div>
                  <div className="form-check">
                    <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" defaultChecked />
                    <label className="form-check-label" htmlFor="flexRadioDefault2">
                      Nữ
                    </label>
                  </div>
                  <div className="form-check">
                    <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault3" defaultChecked />
                    <label className="form-check-label" htmlFor="flexRadioDefault3">
                      Khác
                    </label>
                    <hr />
                  </div>
                  <button type="submit" className="btn btn-primary">Cập nhật</button>
                  </form>
                  
                </div>
              </div>
            </div>
          </div>
        </div>

    )
}
