import { gql } from '@apollo/client'

const recommendedVideos = gql `
 query recommendedVideos {
     videos {
         title
         id
         thumbnail
         createdAt
         videoLikes{
             id
         }
         view{
             id
         }
         user{
             username
             
         }
     }
 }
`

const videoPlaying = gql `
query videoPlaying(id:ID!) {
    video(id){
        id      
  createdAt    
  title
  description
  url
  thumbnail
  document
  userId
  user:        User
  videoLikes:   [VideoLike!]!
  view:        [View!]!
  comment:      [Comment!]!
        
    }
}
`
export { recommendedVideos }
